// SPDX-License-Identifier: MIT 
pragma solidity ^0.8.0;

uint8 constant MAX_LENGTH = 10;   // massimo numero di contratti pubblicabili e figli ottenibili

enum artistState {NoReq, ReqInviata, ReqAccettata, ReqNegata}    // stati di gestione delle richieste 

struct ArtistReq {
    artistState state;  // stato della richiesta, può asssumere uno dei valori preimpostati
    address EA; // eth Address sender  
    address thisContract;
    bytes32 hash;   // hash dell' econtent a cui ha fatto richiesta
    bool result;    // true se l'artista ha eseguito una richiesta che è stata accettata
}

contract Factory{
    address[] private contracts;    // lista di tutti i contratti pubblicati
    address[] private freezedContracts;   // lista di tutti i contratti bloccati

    mapping (address => bool) private isArtist;   // mappa degli artisti registrati - true se sono registrati - false se non lo sono
    mapping (address => address[]) private artistContracts;    // mappa di tutti i contratti pubblicati da un artista
    mapping (address => address[]) private artistFreezedContracts;  // mappa di tutti i contratti congelati appartenti ad un artista
    mapping (address => address[]) private childContractsObtained;   // mappa dei contratti figli che un utente ha ottenuto
    mapping (address => address[]) private childContractsGiven;    // mappa dei contratti figli che un artista ha concesso
    mapping (address => ArtistReq[]) private artistRequestsReceived; // mappa delle richieste ricevute da un artista
    mapping (address => ArtistReq[]) private artistRequestsSent; // mappa delle richieste inviate da un artista
    mapping (bytes32 => uint) private indexArtistRequestsReceived;    // mappa che indicizza l'hash univoco di una richiesta con un indice
    mapping (bytes32 => uint) private indexArtistRequestsSent;    // mappa che indicizza l'hash univoco di una richiesta con un indice


    // eventi factory contract
    event ArtistAdded(string info, address indexed artist);
    event DeployedContracts(string info, address indexed owner, address contractAddress);
    event ChildCreated(string info, address indexed childOwner, address childAddress, address parentArtist, address parentContract);

    // eventi content contract
    event RegisteredReq(string info, address sender, address contractAddress, address owner);
    event GrantedPerm(string info, address sender, address contractAddress, address owner);
    event DeniedPerm(string info, address sender, address contractAddress, address owner);
    event FreezedContract(string info, address indexed owner, address contractAddress);

    // esegue il deploy di uno smart contract associandolo all'artista che ha chiamato il metodo
    function createContract(string memory IPFSHash) public {
        require(isArtist[msg.sender], "Artista non presente in lista");    // solo artisti registrati possono eseguire il deploy di contratti
        require(artistContracts[msg.sender].length < MAX_LENGTH, "Non possibile creare un contratto, limite raggiunto"); // limito la quantità di contratti possedibili da un artista

        address newContract = address(new Content(msg.sender, keccak256(abi.encodePacked(IPFSHash)), address(this)));    // creazione contratto con proprietario il chiamante del metodo e l'hash del video (Converto una stringa in hash)
        artistContracts[msg.sender].push(newContract);    // aggiungo il contratto alla lista dei contratti posseduti dall'artista
        contracts.push(newContract);    // aggiungo il contratto alla lista di tutti i contratti
        emit DeployedContracts("Contratto pubblicato: (owner, contractAddress)", msg.sender, newContract);    // deploy del contratto completato
    }

    function createChildContract(address senderAddress, address parentArtist, bytes32 IPFShash, address partentContract, uint256 timestamp) external {        
        ChildContent child = new ChildContent(senderAddress, parentArtist, IPFShash, partentContract, timestamp);   // creo il contratto figlio
        childContractsObtained[senderAddress].push(address(child));  // aggiunge l'indirizzo del figlio alla mappa del padre
        childContractsGiven[parentArtist].push(address(child));  // aggiunge l'indirizzo del figlio alla mappa del padre
        emit ChildCreated("Contratto figlio pubblicato: (childOwner, childAddress, parentArtist, parentContract)", senderAddress, address(child), parentArtist, partentContract); // contratto figlio creato
    }

    function registerArtist() public {  
        require(!isArtist[msg.sender], "Artista presente in lista");    // per registrare un nuovo artista questo non può essere già registrato

        isArtist[msg.sender] = true; // imposto il valore dell'artista sulla mappa a true
        emit ArtistAdded("Artista aggiunto in lista: (artistAddress)", msg.sender); // artista aggiunto con successo
    }

    function getIsArtist(address user) public view returns (bool) {
        return isArtist[user];
    }

    function getArtistContracts(address artist) public view returns (address[] memory) {
        return artistContracts[artist];
    }

    function getContracts() public view returns (address[] memory) {
        return contracts;
    }

    function addArtistRequestsReceived(address artist, ArtistReq memory req, bytes32 requestKey) external {
        artistRequestsReceived[artist].push(req);
        indexArtistRequestsReceived[requestKey] = artistRequestsReceived[artist].length - 1;    // salvo l'indice della richiesta ricevuta
    }

    function getArtistRequestsReceived(address artist) public view returns (ArtistReq[] memory) {
        return artistRequestsReceived[artist];
    }

    function addArtistRequestsSent(address artist, ArtistReq memory req, bytes32 requestKey) external {
        artistRequestsSent[artist].push(req);
        indexArtistRequestsSent[requestKey] = artistRequestsSent[artist].length - 1;    // salvo l'indice della richiesta inviata
    }

    function getArtistRequestsSent(address artist) public view returns (ArtistReq[] memory) {
        return artistRequestsSent[artist];
    }

    function getChildContractsObtained(address artist) public view returns (address[] memory) {
        return childContractsObtained[artist];
    }

    function getChildContractsGiven(address artist) public view returns (address[] memory) {
        return childContractsGiven[artist];
    }

    function RegisteredReqEvent(address artist, address contractAddress, address owner) external {
        emit RegisteredReq("Richiesta ricevuta (senderAddress, contractAddress, owner)", artist, contractAddress, owner);
    }

    function GrantedPermEvent(address artist, address contractAddress, address owner) external {
        bytes32 requestKey = keccak256(abi.encodePacked(artist, contractAddress, owner));
        artistRequestsReceived[owner][indexArtistRequestsReceived[requestKey]].result = true;    // aggiorno la richiesta ricevuta con l'esito positivo
        artistRequestsReceived[owner][indexArtistRequestsReceived[requestKey]].state = artistState.ReqAccettata;    // aggiorno lo stato della richiesta ricevuta
        artistRequestsSent[artist][indexArtistRequestsSent[requestKey]].result = true;    // aggiorno la richiesta inviata con l'esito positivo
        artistRequestsSent[artist][indexArtistRequestsSent[requestKey]].state = artistState.ReqAccettata;    // aggiorno lo stato della richiesta inviata 
        emit GrantedPerm("Permesso concesso (senderAddress, contractAddress, owner)", artist, contractAddress, owner);
    }

    function DeniedPermEvent(address artist, address contractAddress, address owner) external {
        bytes32 requestKey = keccak256(abi.encodePacked(artist, contractAddress, owner));
        artistRequestsReceived[owner][indexArtistRequestsReceived[requestKey]].state = artistState.ReqNegata;    // aggiorno la richiesta ricevuta con l'esito negativo
        artistRequestsSent[artist][indexArtistRequestsSent[requestKey]].state = artistState.ReqNegata;    // aggiorno la richiesta inviata con l'esito negativo
        emit DeniedPerm("Permesso negato (senderAddress, contractAddress, owner)", artist, contractAddress, owner);
    }

    function addFreezedContracts(address contractAddress, address owner) external {
        freezedContracts.push(contractAddress);
        artistFreezedContracts[owner].push(contractAddress);
        emit FreezedContract("Contratto congelato (owner, contractAddress)", owner, contractAddress);
    }

    function getFreezedContracts() public view returns (address[] memory) {
        return freezedContracts;
    }

    function getArtistFreezedContracts(address artist) public view returns (address[] memory) {
        return artistFreezedContracts[artist];
    }
}

contract Content{
    // il deploy di uno smart contract corrisponde con la pubblicazione di un video/immagine/canzone/...
    // ogni SC si occupa di autenticare l'originalità del econtent e di gestire richieste eseguite da altri artisti/utenti di riutilizzare tale contenuto
    address private _owner;  // proprietario dello SC
    EContent private thisEContent;   // informazioni sul contenuto digitale
    Factory private factory; // contratto factory che ha eseguito il deploy di questo SC
    bool private freeze;    // determina il blocco del contratto, se vale true il contratto rifiuterà tutte le richieste

    struct EContent {
        address owner;  // proprietario del econtent
        bytes32 IPFS_Hash;    // IPFS hash del econtent
        address SC_address;   // indirizzo SC associato all' econtent
        uint256 timestamp;    // data pubblicazione
    }

    mapping (address => ArtistReq) private requests; // mappa che associa la richiesta ricevuta all'artista che l'ha fatta

    event Request (string info, bytes32 requestHash);
    event Reply (string info, bytes32 requestHash);

    modifier onlyOwner(){
        require(msg.sender == _owner);
        _;
    } 

    modifier notOwner(){
        require(msg.sender != _owner);
        _;
    }

    constructor(address sender, bytes32 IPFShash, address factoryAddress){
        factory = Factory(factoryAddress);  // salvo l'istanza del contratto Factory
        _owner = sender;    // il proprietario è colui che ha creato lo SC
        thisEContent.owner = sender;
        thisEContent.IPFS_Hash = IPFShash;
        thisEContent.SC_address = address(this);
        thisEContent.timestamp = block.timestamp;   // salvo l'istante temporale in cui viene creato il blocco
        freeze = false; // blocco del contratto, all'inizio impostato a false
    }

    // richiesta permesso di riutilizzo di un EContent - il proprietario non può fare richiesta per un suo contenuto
    function request() public notOwner{
        require(requests[msg.sender].state == artistState.NoReq, "Ammessa una sola richiesta per utente");    // un utente può fare una sola richiesta per contenuto
        require(freeze == false, "Contratto congelato");   // esegue solo le il contratto non è stato bloccato

        requests[msg.sender].state = artistState.ReqInviata; // aggiorno lo stato della richiesta e ne salvo le informazioni
        requests[msg.sender].EA = msg.sender; 
        requests[msg.sender].thisContract = address(this);
        requests[msg.sender].hash = thisEContent.IPFS_Hash;
        requests[msg.sender].result = false;

        bytes32 requestKey = keccak256(abi.encodePacked(msg.sender, address(this), _owner));
        factory.addArtistRequestsReceived(_owner, requests[msg.sender], requestKey);    // aggiungo la richiesta alla lista di quelle ricevute dal proprietario
        factory.addArtistRequestsSent(msg.sender, requests[msg.sender], requestKey);    // aggiungo la richiesta alla lista di quelle inviate dall'artista
        factory.RegisteredReqEvent(msg.sender, address(this), _owner); // comunico l'avvenuta registrazione della richiesta
        emit Request("Richiesta registrata: ", requestKey);
    }

    // il proprietario dello SC può rispondere alle richieste, accettandole o rifiutandole - richiamabile sono dal proprietario
    function reply(bool result, address senderAddress) public onlyOwner{
        require(requests[senderAddress].state == artistState.ReqInviata, "Richiesta non in attesa di risposta");   // valuto solo richieste che necessitano di risposta

        if(result){ // richiesta accettata
            requests[senderAddress].state = artistState.ReqAccettata;  // aggiorno lo stato della richiesta nella lista
            requests[senderAddress].result = result;
            factory.GrantedPermEvent(senderAddress, address(this), _owner); // comunico l'esito
            factory.createChildContract(senderAddress, msg.sender, thisEContent.IPFS_Hash, thisEContent.SC_address, thisEContent.timestamp); // richiamo il metodo del contratto factory che crea il figlio
        }
        else {  // richiesta rifiutata
            requests[senderAddress].state = artistState.ReqNegata;   // aggiorno lo stato della richiesta generale
            factory.DeniedPermEvent(senderAddress, address(this), _owner); // comunico l'esito
        }
        emit Reply("Risposta inviata: ", keccak256(abi.encodePacked(senderAddress, address(this), _owner)));
    }

    function getOwner() public view returns (address) {
        return _owner;
    }

    function freezeContract() public onlyOwner() {  // congela contratto - non potrà ricevere nuove richieste
        require(freeze == false);   // non posso congelare un contratto già congelato

        freeze = true;
        factory.addFreezedContracts(address(this), msg.sender);    // aggiungo il contratto alla lista di quelli bloccati
    }
}

contract ChildContent {
    // il contratto figlio non può ricevere ed elaborare richieste
    address private _owner;  // proprietario dello SC
    ChildEContent private childContent;  // informazioni del contratto figlio

    struct ChildEContent {
        address owner;  // proprietario del econtent
        bytes32 IPFS_Hash;    // IPFS hash del econtent
        address SC_address;   // indirizzo SC associato all' econtent
        uint256 timestamp;    // data pubblicazione
        Content.EContent parent_EC;    // informazioni del contratto genitore
    }

    modifier onlyOwner(){
        require(msg.sender == _owner);
        _;
    }

    event CangedIPFSHash(string info, bytes32 IPFShash);

    constructor(address senderAddress, address parentArtist, bytes32 IPFShash, address parentContract, uint256 timestamp){
        _owner = senderAddress; // il proprietario dello SC figlio è l'utente a cui è stata accettata la richiesta
        childContent.owner = senderAddress; // il contenuto figlio è di proprietà dell'artista che ha eseguito la richiesta, può modificarlo
        childContent.IPFS_Hash = IPFShash;  // inizializzo l'hash del video figlio uguale al padre, potà farne ciò che vuole
        childContent.SC_address = address(this);    // l'indirizzo del contratto figlio è l'indirizzo del contratto stesso
        childContent.timestamp = block.timestamp;   // salvo l'istante temporale in cui viene creato il blocco
        childContent.parent_EC.owner = parentArtist; // copio le informazioni dello SC padre 
        childContent.parent_EC.IPFS_Hash = IPFShash;
        childContent.parent_EC.SC_address = parentContract;
        childContent.parent_EC.timestamp = timestamp;
    }

    // il proprietario del contenuto figlio ha la possibilità di modificarlo e aggiornare l'hash
    function setIPFSHash(bytes32 IPFSHash) public onlyOwner{
        childContent.IPFS_Hash = IPFSHash;
        emit CangedIPFSHash("IPFShash modificato (IPFSHash)", IPFSHash);
    }
}
